DROP DATABASE IF EXISTS project_db;
CREATE DATABASE IF NOT EXISTS project_db;

use project_db;

DROP TABLE IF EXISTS groups;
CREATE TABLE IF NOT EXISTS groups(
   GroupId INTEGER  NOT NULL PRIMARY KEY AUTO_INCREMENT
  ,GroupName    VARCHAR(50) NOT NULL
);

DROP TABLE IF EXISTS country;
CREATE TABLE IF NOT EXISTS country(
   CountryId INTEGER  NOT NULL PRIMARY KEY AUTO_INCREMENT
  ,CountryName      VARCHAR(24) NOT NULL
);


DROP TABLE IF EXISTS agency;
CREATE TABLE IF NOT EXISTS agency(
   AgencyId INTEGER  NOT NULL PRIMARY KEY AUTO_INCREMENT
  ,AgencyName     VARCHAR(100) NOT NULL
);

DROP TABLE IF EXISTS product;
CREATE TABLE IF NOT EXISTS product(
   SKU       INTEGER  NOT NULL PRIMARY KEY AUTO_INCREMENT
  ,Name      VARCHAR(60) NOT NULL
  ,Price     VARCHAR(9) NOT NULL
  ,Case_size VARCHAR(13) NOT NULL
  ,CountryId INTEGER  NOT NULL
  ,AgencyId  INTEGER  NOT NULL
  ,GroupId   INTEGER  NOT NULL
  ,Info      VARCHAR(500)
  ,FOREIGN KEY (CountryId) REFERENCES country(CountryId)
  ,FOREIGN KEY (AgencyId)  REFERENCES agency(AgencyId)
  ,FOREIGN KEY (GroupId)   REFERENCES groups(GroupId)
  ON UPDATE CASCADE ON DELETE RESTRICT
);

set foreign_key_checks =0;
